function fixHeader(){let e=document.getElementById("header-section");window.pageYOffset>e.offsetTop?e.classList.add("fixed-header"):e.classList.remove("fixed-header")}function toggleMenu(){let e=document.getElementById("nav-icon"),t=document.getElementById("nav-menu");e.classList.contains("open")?(e.classList.remove(["open"]),t.classList.remove(["open"])):(e.classList.add("open"),t.classList.add("open"))}function toggleVideo(){let e=document.getElementById("video"),t=document.getElementById("play-btn");e.paused?(e.play(),t.style.display="none"):(e.pause(),t.style.display="block")}window.onscroll=function(){fixHeader()};